mapper.update:
update international_cvs_file_path
set deleted = 1,update_time = now()
where id=#{id}

mapper.select:
SELECT id,customer_id,active_number,no_register_number,unknown_number,total_number,deleted, create_time,update_time,active_file_path,no_register_file_path,unknown_file_path,zip_name,zip_path,zip_size from international_cvs_file_path where customer_id = #{customerId} and deleted = 0
and create_date >= #{createTimeFrom}
and create_date <= #{createTimeEnd}
order by id desc
